import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../services/api";
import TaskList from "./TaskList";

export default function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("all");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const location = useLocation();
  const navigate = useNavigate();

  // âœ… Handle token from URL and store in localStorage
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const token = params.get("token");
    if (token) {
      localStorage.setItem("token", token);
      navigate("/dashboard", { replace: true }); // clean URL after storing token
    }
  }, [location, navigate]);

  // âœ… Check for token in localStorage on first mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      console.warn("No token found â€” redirecting to login");
      navigate("/login");
    }
  }, [navigate]);

  // âœ… Fetch tasks
  const fetchTasks = async () => {
    try {
      setLoading(true);
      const res = await api.get("/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load tasks");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // âœ… Apply filters to tasks
  const filteredTasks = tasks.filter((task) => {
    const now = new Date();
    const due = new Date(task.dueDate);
    if (filter === "today") return due.toDateString() === now.toDateString();
    if (filter === "overdue") return due < now && task.status !== "completed";
    if (filter === "completed") return task.status === "completed";
    return true;
  });

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-semibold mb-4">ðŸ“‹ Your Tasks</h2>

      <div className="flex gap-3 mb-4">
        {["all", "today", "overdue", "completed"].map((f) => (
          <button
            key={f}
            onClick={() => setFilter(f)}
            className={`px-4 py-2 rounded ${
              filter === f ? "bg-blue-600 text-white" : "bg-white border"
            }`}
          >
            {f.toUpperCase()}
          </button>
        ))}
      </div>

      {loading && <p>Loading tasks...</p>}
      {error && <p className="text-red-500">{error}</p>}
      {!loading && filteredTasks.length === 0 && <p>No tasks to show.</p>}

      <TaskList tasks={filteredTasks} />
    </div>
  );
}
